import {React, useState, useEffect} from 'react';
import BlogPost from '../Components/BlogPost.js'

function Main() {
    const [Posts, setPosts] = useState(null);

    //runs only on initialisation
    useEffect( () => {
        fetch('http://localhost:3001/allblogs').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                console.log('gotallposts')
                const Posts = response.res
                setPosts(Posts)
            }
        )
    }, [])
    
    function clicked() {
        console.log(Posts)
        var Postz = Posts
        Postz[Posts.length] = 'bruh'
        setPosts(Postz)
    }
    return (
        <div>
            <button onClick={clicked}>hi</button>
            <BlogPost />
        </div>
    )
}

export default Main;
