function UpdateCategory(props) {
    const OGPost = props.OGPost;
    const OGCats = props.OGCats;
    const AllCats = props.AllCats;

    var DisplayCats = [];

    //getting data about which categories can and cannot be selected
    OGCats.map(Cat => {
        return (
            <div key={Cat.categoryid}>
                <input type='checkbox' onClick={() => props.AddCat(Cat.categoryid)} />
                {Cat.category}
            </div>
        )
    })
}

export default UpdateCategory;
