import {React, useState, useEffect, useRef} from 'react';

function Category(props) {
    const [isChecked, setChecked] = useState(false)
    const firstrender = useRef(true);

    //runs only on mount to check if the Category is All, since we want All to be initially selected
    useEffect(() => {
        if (props.Cat.categoryid == 0) {
            setChecked(true)
        }
    }, [])

    useEffect(() => {
        console.log(firstrender.current)
        if (!firstrender.current) {
            UpdateCat()
            firstrender.current = false
        }
    }, [isChecked])

    function CatClicked() {
        setChecked(!isChecked)
    }

    function UpdateCat() {
        if (isChecked) {
            props.AddCatSelection(props.Cat)
        }

        if (!isChecked) {
            props.RemoveCatSelection(props.Cat)
        }
    }

    return (
        <div>
            <input type='checkbox' onClick={CatClicked} checked={isChecked} />
            {props.Cat.category}
        </div>
    )

}

export default Category;
