import {React, useState, useEffect} from 'react';
import BlogPost from '../Components/BlogPost.js'

function Main(props) {
    const [Posts, setPosts] = useState(null)

    function OpenPost(PostID) {
        props.OpenPost(PostID)
    }

    //runs only on initialisation
    useEffect( () => {
        fetch('http://localhost:3001/allblogs').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                const Posts = response.res
                setPosts(Posts)
            }
        )
    }, [])

    return (
        <div>
            {Posts ? Posts.map( (Post) => {
                return (
                    <div>
                        <button onClick={OpenPost}>
                            <BlogPost key= {Post.PostID} Title={Post.Title} PostText={Post.PostText} ImageName={Post.ImageName} Image={Post.Image} Username={Post.Username} OpenPost={OpenPost} />
                        </button>
                        <br />
                    </div>
                )
            }) : null}
        </div>
    )
}

export default Main;
