import {useLocation} from 'react-router-dom';

function OpenBlog() {
    const Data = useLocation().state; //get data about Post from App.js

    return (
        <div>
            <h1>Title: {location.state.Title}</h1> <br />
            Author: {location.state.Username} <br /><br />
            <img src={`data:image/jpeg;base64,${location.state.Image}`} alt={location.state.ImageName} /> <br /> {/* Display the image base64 image data as an image */}
            {location.state.PostText} <br />
        </div>
    )
}

export default OpenBlog;
