import { React, useRef, useState } from 'react';

function Signup() {

    function onSubmit() {
        console.log(useRef.current.value)
    }

    return (
        <div>
            Welcome to the Signup page! <br />
            <form onSubmit={onSubmit}>
                New Username: <input type='text' required ref={NUserRef} /> <br />
                New Password: <input type='text' required ref={NPasswordRef} /> <br />
                Confirm Password: <input type='text' requried ref={NPassword2Ref} /> <br />
                Display Name: <input type='text' required ref={NDisplay} /> <br />
                <input type='submit' value='signup' />
            </form>
        </div>
    )
}

export default Signup;
