        //         return res.json()
        //     }
        // ).then(
        //     response => {
        //         const Response = response.res
        //         const Display = response.Display
        //         if (Response) {
        //             props.Login(Username, Display)
        //         } else{
        //             setState(false)
        //         }
        //     }
        // )

        fetch('http://localhost:3001/postblog', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({'hello': 'hi'})
            //body: Image
        }).then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                console.log(response.res)
            }
        )

    }

    return (
        <div>
            Welcome to the Login page! <br />
            { isValid ? null : <div>Incorrect Username or Password!</div> }
            <form onSubmit={onSubmit}>
                Username: <input type='text' required ref={UsernameRef}/> <br />
                Password: <input type='text' required ref={PasswordRef}/> <br />
                <input type='submit' value='login' />
            </form>
        </div>
    )
}

export default Login;
