function Comment(props) {
    async function DeleteComment() {
        //send a POST request to /deletecomment with CommentID in its body
        await fetch('http://localhost:3001/deletecomment', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({CommentID: props.CommentData.commentid})
        })
    }

    return (
        //Display the comment
        <div>
            User: {props.CommentData.displayname} <br />
            Comment: {props.CommentData.comment} <br />
            <button onClick={DeleteComment}>Delete Comment</button> <br />
        </div>
    )
}

export default Comment;
