import LoginPage from './Pages/Login.js';
import MainPage from './Pages/Main.js';
import SignupPage from './Pages/Signup.js';
import PostBlogPage from './Pages/PostBlog.js';
import OpenedBlogPage from './Pages/OpenedBlog.js'
import {Routes, Route, useNavigate, Link} from 'react-router-dom';
import {React, useState} from 'react';

var CurrUser = null;
var Display = null;

function App() {
  console.log(CurrUser)

  const navigate = useNavigate();
  const [isLogin, setLoginState] = useState(false);
  const [OpenedPost, setOpenedPostState] = useState(null);

  function Login(Username, DisplayName) {
    CurrUser = Username
    Display = DisplayName
    setLoginState(true)
    navigate('/')
  }

  function Logout(){
    Display = null;
    CurrUser = null;
    setLoginState(false)
    navigate('/')
  }

  function OpenPost(Post) {
    const FD = new FormData()
    FD.append('PostID', Post.PostID)
    fetch('http://localhost:3001/getcomments').then(
      res => {
        return res.json()
      }
    ).then(
      response => {
        const Comments = response.res
        Post.Comments = Comments
        setOpenedPostState(Post)
        console.log(Post)
        navigate('/Post')
      }
    )
  }

  return (
    <div>
      { isLogin ? <div>Hello, {Display}!</div> : null }
      <Link to='/'>Home</Link>
      <Link to='signup'>Singup</Link>
      <Link to='/login'>Login</Link>
      { isLogin ? <Link to='/postblog'>Post</Link> : null }
      { isLogin ? <button onClick={Logout}>Logout</button> : null }

      <Routes>
        <Route path='/' exact element={<MainPage OpenPost={OpenPost} />} />
        <Route path='Post' exact element={<OpenedBlogPage Post={OpenedPost} />} />
        <Route path='/signup' exact element={<SignupPage Login={Login} />} />
        <Route path='/login' exact element={<LoginPage Login={Login} />} />
        <Route path='/postblog' exact element={<PostBlogPage Author={CurrUser}/>} />
      </Routes>
    </div>
  );
}

export default App;
