import {React, useState, useEffect} from 'react';
import BlogPost from '../Components/BlogPost.js'

function Main() {
    const [Posts, setPosts] = useState(
        fetch('http://localhost:3001/allblogs').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                const Posts = response.res
                setPosts(Posts)
            }
        )
    );
    console.log(Posts.then())
    // //runs only on initialisation
    // useEffect( () => {
