    //             return res.json()
    //         }
    //     ).then(
    //         response => {
    //             const Posts = response.res
    //             setPosts(Posts)
    //         }
    //     )
    // }, [])

    return (
        <div>
            {Posts.then(map( (Post) => {return <BlogPost Title={Post.Title} PostText={Post.PostText} Image={Post.Image} Username={Post.Username} />}))}
        </div>
    )
}

export default Main;
