import Comment from './Comment.js';

function AllComments(props) {
    return (
        <div>
            <h1>Comments Section: </h1>
            {(props.Comments ? props.Comments.map(CommentData => { //iterates through the Comments prop, getting the CommentData of every comment consisting of its comment and commentor displayname
                return <div key={CommentData.commentid}> <Comment DisplayName={CommentData.displayname} Comment={CommentData.comment} /> <br /> </div> //renders a Comment component for each CommentData
            }) : <div> sorry, but there are no comments </div>)} {/* if the Comments prop is null, display a sorry, no comments message */}
        </div>
    )
}

export default AllComments;
