import {useState, useRef} from 'react';

function UpdateCategory(props) {
    const Cat = props.Cat;
    console.log(props.Checked)

    function UpdateCateSelection(CatID) {
        if (props.Checked == true) {
            props.RemoveCat(CatID)
        } else {
            props.AddCat(CatID)
        }
    }

    return (
        <div key={Cat.categoryid}>
            <input type='checkbox' onClick={() => UpdateCateSelection(Cat.categoryid)} checked={props.Checked} />
            {Cat.category}
        </div>
    )
}

export default UpdateCategory;
