    const NDisplay = useRef();

    function onSubmit() {
        console.log(useRef.current.value)
    }

    return (
        <div>
            Welcome to the Signup page! <br />
            <form onSubmit={onSubmit}>
                New Username: <input type='text' ref={NUserRef} /> <br />
                New Password: <input type='text' ref={NPasswordRef} /> <br />
                Confirm Password: <input type='text' ref={NPassword2Ref} /> <br />
                Display Name: <input type='text' ref={NDisplay} /> <br />
                <input type='submit' value='signup' />
            </form>
        </div>
    )
}

export default Signup;
