import Comment from './Comment.js';

function AllComments(props) {
    return (
        <div>
            {(props.Comments ? props.Comments.map(CommentData => {
                <Comment key={CommentData.commentid} DisplayName={CommentData.displayname} Comment={CommentData.comment} />
            }) : <div> sorry, but there are no comments </div>)}
        </div>
    )
}

export default AllComments;
