import {React, useState, useEffect} from 'react';
import BlogPost from '../Components/BlogPost.js'

function Main() {
    const [Posts, setPosts] = useState(null);

    //runs only on initialisation
    useEffect( () => {
        fetch('http://localhost:3001/allblogs').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                const Posts = response.res
                setPosts(Posts)
            }
        )
    }, [])

    return (
        <div>
            {Posts.map( (Post) => {<BlogPost Title={Post.Title} PostText={Post.PostText} Image={Post.Image} Username={Post.Username} />})}
        </div>
    )
}

export default Main;
