import {React, useState, useEffect} from 'react';
import BlogPost from '../Components/BlogPost.js'

function Main(props) {
    const [Posts, setPosts] = useState(null)

    //runs only on initialisation
    useEffect( () => {
        fetch('http://localhost:3001/allblogs').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                const Posts = response.res
                setPosts(Posts)
            }
        )
    }, [])

    return (
        <div key={1}>
            {Posts ? Posts.map(Post => {

                    <div key={Post.PostID}>
                        <button>
                            <BlogPost Post={Post} />
                        </button>
                        <br />
                    </div>
                
            }) : <div>There are no posts yet :(</div> }
        </div>
    )
}

export default Main;
