function Comment(props) {
    const isViewerAuthor = (Data.CurrUser == props.CommentData.username); //isViewerAuthor is true if Data.CurrUser = Data.Username, AKA the viewer is the author of the post

    async function DeleteComment() {
        props.DeleteComment(props.CommentData.commentid)
    }

    return (
        //Display the comment
        <div>
            User: {props.CommentData.displayname} <br />
            Comment: {props.CommentData.comment} <br />
            {isViewerAuthor ? 
            <div>
                <button onClick={DeleteComment}>Delete Comment</button> <br />
            </div> : null}
        </div>
    )
}

export default Comment;
