import { React, useRef, useState } from 'react';

function Signup() {
    const NUserRef = useRef();
    const NPasswordRef = useRef();
    const NPassword2Ref = useRef();
    const NDisplay = useRef();

    const [isPWValid, setPWState] = useState(true);
    const [isUserValid, setUserState] = useState(true);

    function onSubmit(event) {
        event.preventDefault();

        if (NPassword2Ref !== NPasswordRef) {
            setPWState(false)
        }
    }

    return (
        <div>
            Welcome to the Signup page! <br />
            { isValid ? <div>The Username is unavailable!</div> : false }
            { isValid ? <div>The Passwords do not match!</div> : false }
            <form onSubmit={onSubmit}>
                New Username: <input type='text' required ref={NUserRef} /> <br />
                New Password: <input type='text' required ref={NPasswordRef} /> <br />
                Confirm Password: <input type='text' required ref={NPassword2Ref} /> <br />
                Display Name: <input type='text' required ref={NDisplay} /> <br />
                <input type='submit' value='signup' />
            </form>
        </div>
    )
}

export default Signup;
