import {useState, useRef} from 'react';

function UpdateCategory(props) {
    const OGPost = props.OGPost;
    const OGCats = props.OGCats;
    const AllCats = props.AllCats;
    
    OGCats.map(Cat => {
        return (
            <div key={Cat.categoryid}>
                <input type='checkbox' onClick={() => UpdateCateSelection(Cat.categoryid)} checked={props.Checked} />
                {Cat.category}
            </div>
        )
    })
}

export default UpdateCategory;
