import Image from '../Images/Beans.webp';

function BlogPost(props) {
    function OpenPost(){
        props.OpenPost(props.PostID)
    }
    return (
        <div>
            <h1>Title: {props.Title}</h1> <br />
            Author: {props.Username} <br />
            <img src={`data:image/jpeg;base64,${props.Image}`} alt={props.ImageName} /> <br />
            {props.PostText} <br />
            <button onClick={SeeComments}>Comments</button>
        </div>
    )
}

export default BlogPost;
