import {React, useState, useEffect} from 'react';
import BlogPost from '../Components/BlogPost.js'

function Main() {
    const [Posts, setPosts] = useState(null)

    function SeeComments(PostID) {
        fetch('http://localhost:3001/fetchcomments').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                const Posts = response.res
                setPosts(Posts)
            }
        )
    }

    //runs only on initialisation
    useEffect( () => {
        fetch('http://localhost:3001/allblogs').then(
            res => {
                return res.json()
            }
        ).then(
            response => {
                const Posts = response.res
                setPosts(Posts)
            }
        )
    }, [])

    return (
        <div>
            <button onClick={SeeComments}>
            {Posts ? Posts.map( (Post) => {return <BlogPost key= {Post.PostID} Title={Post.Title} PostText={Post.PostText} ImageName={Post.ImageName} Image={Post.Image} Username={Post.Username} SeeComments={SeeComments} />}) : null}
            </button>
        </div>
    )
}

export default Main;
